import { createApp } from "vue";
import App from "./App.vue";
import router from "./router/index.js";
import { createVuestic } from 'vuestic-ui'
import { createStore } from "vuex";
import 'vuestic-ui/css'
import { library } from '@fortawesome/fontawesome-svg-core'


/* import font awesome icon component */
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome'
import { faPencilAlt, faPhone } from "@fortawesome/free-solid-svg-icons";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { faIdCard } from "@fortawesome/free-solid-svg-icons";
import { faClipboardList } from "@fortawesome/free-solid-svg-icons";
import { faFilter } from "@fortawesome/free-solid-svg-icons";
import { faSort } from "@fortawesome/free-solid-svg-icons";

library.add(faClipboardList)
library.add(faSort)
library.add(faFilter)
library.add(faPhone)
library.add(faIdCard)
library.add(faPencilAlt)
library.add(faPlus)
library.add(faTrash)
library.add(faSearch)


const store = createStore({
    state: {
        selectedStatus: "",selectedOrder:"",selectedID:0,
        selectedTodo:localStorage.getItem("selectedTodo")
        // {


          // "id":"" ,
          // "todo": "",
          // "status": "",
          // "email": "",
          // "version": ""
        // }
      },
      mutations: {
        pending (state) {
          state.selectedStatus='PENDING'
        },
        completed (state) {
            state.selectedStatus='COMPLETED'
          },
          ascend (state) {
            state.selectedOrder='asc'
          },
          descend (state) {
            state.selectedOrder='desc'
          },
          all(state){
            state.selectedStatus=""
          },
          setId(state,id){
            state.selectedID=id
          },
          setSelectedTodo(state,todoObj){
            state.selectedTodo=todoObj
          }
  },

});


const app = createApp(App).component('font-awesome-icon', FontAwesomeIcon);
app.use(store)

app.use(router);


app.use(createVuestic());
app.mount("#app");
